function formatDate(date) {
var d = new Date(date),
month = '' + (d.getMonth() + 1),
day = '' + d.getDate(),
year = d.getFullYear();

if (month.length < 2) month = '0' + month;
if (day.length < 2) day = '0' + day;

return [year, month, day].join('');
}

var tnow = +new Date();
var tminus24 = tnow - (24 * 60 * 60 * 1000);

msg.payload = {
"models": msg.payload["data"].models,
"start": formatDate(tminus24),
"finish": formatDate(tnow),
"lon1": 112.8500,
"lat1": -9.8600,
"lon2": 153.6900,
"lat2": -43.7000
}

return msg;

// Test Data Example Defaults

let time_step =1000*60*60*1; // ie hourly in milliseconds
var samples_per_series = 24*7;
let noise_variation = Math.random()*0.5;
 // -6.73 per hour

var num_models = 3;
var model_names = ['Nolan', 'van Dijk', 'Boer', 'McArthur'];

var lat = 0;
var lng = 0;

var now = new Date();
var start = new Date(now.getTime() - (samples_per_series*time_step) );
var finish = new Date(now.getTime());
var timeseries = [];

// Override if values supplied in request
if(msg.req.params.models) {
    var names = msg.req.params.models.split(',');
    num_models = names.length;
    model_names = names;
}
if(msg.req.params.lng) lng = msg.req.params.lng;
if(msg.req.params.lat) lat = msg.req.params.lat;
if(msg.req.params.start) start = msg.req.params.start;
if(msg.req.params.finish) finish = msg.req.params.finish;

for(model=0;model<num_models;model++) {

    var sample = {};
    sample.name = model_names[model];


    var meta = {};
    meta.lat = lat;
    meta.lng = lng;
    meta.start = start;
    meta.finish = finish;
    meta.rainfall = "Expressed as percentage gain in water saturation";
    meta.spatial_resolution = 'Average over 500 m^2';
    meta.temporal_granularity = time_step + ' milliseconds';
    // TODO - other metadata like applicable fuel types etc

    // Inject metadata
    sample.meta = meta;
    sample.series = [];

    var drying_acceleration = Math.random()*0.43;

    var raining = false;

    for(observation=0;observation<samples_per_series;observation++) {
        var transpiration = (Math.random()*0.87235623);
        // var time_noise = Math.random()*0.00125;
        var time_noise = 0.0;
        var exposure = Math.random()*0.363124;
        var record={};
        var fuel;
        var loss=0;
        var rain_chance = Math.random();
        var rainfall = 0;


        // First pass check
        if(model===0) {

            // Lay down some randomness...
            if(observation<1) {
            // First Observation
                fuel = Math.random() * 100;
            } else {
                fuel = sample.series[observation-1].value; // fill-forward and mutate later
            }

            // in Model[0] we store rainfall

            if(!raining && rain_chance > 0.85) {
                // Start raining
                raining = true;
                rainfall = (Math.random()*45); // % Saturation in one hour (downpour)
            } else if(raining && rain_chance < 0.25) {
                // Stop raining
                raining = false;
            } else if(raining) {
                // Continue raining
                rainfall = (Math.random()*15); // % Saturation in one hour (drizzle)
            } else if (!raining) {
                drying_acceleration += Math.random()*exposure;
            }

        } else {
            // Subsequent Models

            rainfall = timeseries[0].series[observation].rainfall;
            if(rainfall===0) drying_acceleration += Math.random()*exposure;
            // Observations are derived from their parameters 
            // Continue trend from Previous in series

            // Add noise and drift
            if(observation<1) {
                fuel = timeseries[0].series[observation].value;
            } else {
                fuel = sample.series[observation-1].value; // fill-forward and mutate later
            }
        }





        loss = (transpiration + drying_acceleration); // Fudge!!

        fuel += rainfall*(1 - Math.random()*0.1);
        fuel -= loss*(1 - Math.random()*0.25);

        if(fuel > 100) {
            fuel = 100;
        } else if(fuel < 0) {
            fuel = 0;
        }

        var min = Math.max(0, fuel-loss);
        var max = Math.min(100, fuel+rainfall);




        // Flow *forwads* through time from most distant past observation to most recent
        var date_time = new Date(now.getTime() - ((samples_per_series-observation)*time_step) );

        record.name= date_time;
        record.rainfall = rainfall;
        record.value = fuel;

        if(observation==samples_per_series-1) {
            record.min = 0;
            record.max = 100;
        } else {
            record.min = min;
            record.max = max;
        }
        sample.series.push(record);
        // console.log('Series for observation: '+observation+', of model('+model+'):'+model_names[model]+' generated.');
    }
    timeseries.push(sample);

    console.log(timeseries)
    // console.log('Sample for model('+model+'):'+model_names[model]+' generated.');
}




msg.payload = timeseries;

return msg;

// TODO - logic for model selection

// Dead FM
msg.payload = 'DeadFMThreaded.py ' + msg.req.params.lng + ' ' + msg.req.params.lat + ' ' + msg.req.params.start + ' ' + msg.req.params.end;

return msg;

msg.filename="register/bom023/rKCm6dbQ/adfd/IDV71199_VIC_WindMagKmh_1000mAMSL.nc.gz";
msg.localFilename = "/tmp/IDV71199_VIC_WindMagKmh_1000mAMSL.nc.gz";

return msg;

// ID strings from http://www.bom.gov.au/catalogue/anon-ftp.shtml

var object_shortlist = msg.payload.filter(function(item) {
    return (item.hasOwnProperty("name"));
});

msg.path = msg.filename;

msg.filename = object_shortlist.map(function(item) {
    return msg.path + "/" + item.name;    
});

return msg;

if (msg.req.files[0].mimetype.includes('image')) {
    msg.payload = `<img src="data:image/gif;base64,${msg.payload.toString('base64')}">`;
} else {
    msg.payload = msg.payload.toString();
}

return msg;

